import React, { useContext, useState } from "react";
import axios from "axios";

import Sidebar from "../../components/sidebar/Sidebar";
import "./settings.css";
import { Context } from "../../context/Context";

export default function Settings() {
  const { user, dispatch } = useContext(Context);
  const PF = "http://localhost:8000/images/";

  const [file, setFile] = useState(null);
  const [username, setUsername] = useState(`${user.username}`);
  const [email, setEmail] = useState(`${user.email}`);
  const [password, setPassword] = useState(`${user.password}`);

  const [success, setSuccess] = useState(false);

  // console.log(URL.createObjectURL(file));
  const handleSubmit = async (e) => {
    dispatch({ type: "UPDATE_START" });
    e.preventDefault();
    const updatedUser = {
      userId: user._id,
      username,
      email,
      password,
    };
    if (file) {
      const data = new FormData(); // Creating new FormData variable to store the filename and the file
      const filename = Date.now() + file.name; // Custom file name
      data.append("name", filename); // Adding the filename and file to the FormData
      data.append("file", file);
      updatedUser.profilePic = filename;

      try {
        await axios.post("/upload", data); // Uploading the FormData created to the DB via the API
      } catch (error) {
        console.log(error);
      }
    }
    try {
      const res = await axios.put("/users/" + user._id, updatedUser);
      setSuccess(true);
      dispatch({ type: "UPDATE_SUCCESS", payload: res.data });
    } catch (error) {
      console.log(error);
      dispatch({ type: "UPDATE_FAILURE" });
    }
  };

  return (
    <div className="settings">
      <div className="settingsWrapper">
        <div className="settingsTitle">
          <span className="settingsUpdateTitle">Update Your Account</span>
          <span className="settingsDeleteTitle">Delete Account</span>
        </div>
        <form className="settingsForm" onSubmit={handleSubmit}>
          <label>Profile Picture</label>
          <div className="settingsPP">
            <img
              src={file ? URL.createObjectURL(file) : PF+user.profilePic}
              alt=""
            />
            <label htmlFor="fileInput">
              <i className="settingsProfileIcon fa-regular fa-circle-user"></i>
            </label>
            <input
              type="file"
              id="fileInput"
              style={{ display: "none" }}
              onChange={(e) => {
                console.log(e.target.files[0]);
                setFile(e.target.files[0]);
              }}
            />
          </div>
          <label>Username</label>
          <input
            type="text"
            placeholder={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <label>Email</label>
          <input
            type="email"
            placeholder={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <label>Password</label>
          <input
            type="password"
            required
            onChange={(e) => setPassword(e.target.value)}
          />
          <label>Bio</label>
          <textarea maxLength="50" cols="30" rows="5" className="userBio"></textarea>
          <button className="settingsSubmit" type="submit">
            Update
          </button>
          {success && (
            <span className="updatedMessageText">
              Profile updated successfully!
            </span>
          )}
        </form>
      </div>
      <Sidebar />
    </div>
  );
}
