import axios from "axios";
import React, { useState } from "react";
import { Link } from "react-router-dom";

import "./register.css";

export default function Register() {
  const [userInput, setUserInput] = useState({
    username: "",
    email: "",
    password: "",
  });
  const [error, setError] = useState("");

  const handleSubmit = async (event) => {
    event.preventDefault(); // Prevents the page from refreshing when clicked on submit
    setError("");
    try {
      const res = await axios.post("/auth/register", {
        username: userInput.username,
        email: userInput.email,
        password: userInput.password
      })
    } catch (error) {
      setError(error.res);
      console.log(error);
    }
  }

  return (
    <div className="register">
      <span className="registerTitle">REGISTER</span>
      <form className="registerForm" onSubmit={handleSubmit}>
        <label>Username</label>
        <input
          className="registerInput"
          type="text"
          placeholder="Enter your username..."
          onChange={(event) => {
            setUserInput({ ...userInput, username: event.target.value });
          }}
        />
        <label>Email</label>
        <input
          className="registerInput"
          type="email"
          placeholder="Enter your email..."
          autoComplete="none"
          onChange={(event) => {
            setUserInput({ ...userInput, email: event.target.value });
          }}
        />
        <label>Password</label>
        <input
          className="registerInput"
          type="password"
          onChange={(event) => {
            setUserInput({ ...userInput, password: event.target.value });
          }}
        />
        <button className="registerButton">REGISTER</button>
      </form>
      <button className="registerLoginButton registerButton" type="submit">
        <Link className="link" to="/login">
          LOGIN
        </Link>
      </button>
      <p>{error.response.data}</p>
    </div>
  );
}
