import React, { useContext, useState } from "react";
import axios from "axios";

import Sidebar from "../../components/sidebar/Sidebar";
import "./settings.css";
import { Context } from "../../context/Context";

export default function Settings() {
  const { user } = useContext(Context);

  const [file, setFile] = useState(null);
  const [username, setUsername] = useState("null");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    const updatedUser = {
      userId: user._id,
      username,
      email,
      password,
    };
    if (file) {
      const data = new FormData();                  // Creating new FormData variable to store the filename and the file
      const filename = Date.now() + file.name;      // Custom file name
      data.append("name", filename);                // Adding the filename and file to the FormData
      data.append("file", file);
      updatedUser.profilePic = filename;

      try {
        await axios.post("/upload", data);          // Uploading the FormData created to the DB via the API
      } catch (error) {}
    }
    try {
      await axios.post("/users/"+user._, updatedUser);
    } catch (error) {

    }
  };

  return (
    <div className="settings">
      <div className="settingsWrapper">
        <div className="settingsTitle">
          <span className="settingsUpdateTitle">Update Your Account</span>
          <span className="settingsDeleteTitle">Delete Account</span>
        </div>
        <form className="settingsForm">
          <label>Profile Picture</label>
          <div className="settingsPP">
            <img src={user.profilePic} alt="profilePicture" />
            <label htmlFor="fileInput">
              <i className="settingsProfileIcon fa-regular fa-circle-user"></i>
            </label>
            <input type="file" id="fileInput" style={{ display: "none" }}/>
          </div>
          <label>Username</label>
          <input type="text" placeholder="MG"/>
          <label>Email</label>
          <input type="email" placeholder="mg13@gmail.com"/>
          <label>Password</label>
          <input type="password" />
          <button className="settingsSubmit">Update</button>
        </form>
      </div>
      <Sidebar />
    </div>
  );
}
