import React, { useContext, useEffect, useState } from "react";
import axios from "axios";
import { useLocation, Link } from "react-router-dom";

// import postImage from "../../assets/pexels-pixabay-267355.jpg";
import "./singlePost.css";
import { Context } from "../../context/Context";

export default function SinglePost() {
  const loc = useLocation(); // Gets the path of the webpage
  const postId = loc.pathname.split("/")[2]; // Splits the pathname and gets the postId from it
  // console.log(postId);

  const { user } = useContext(Context);
  const PF = "http://localhost:8000/images/";

  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");
  const [updateMode, setUpdateMode] = useState(false);

  const [post, setPost] = useState({});
  useEffect(() => {
    const getPosts = async () => {
      const res = await axios.get("/posts/" + postId);
      // console.log(res);
      setPost(res.data);
    };
    getPosts();
  }, [postId]);

  const handleDelete = async () => {
    try {
      await axios.delete(`/posts/${post._id}`, {
        data: { username: user.username },
      });
      window.location.replace("/");
    } catch (error) {
      console.log(error);
    }
  };


  return (
    <div className="singlePost">
      <div className="singlePostWrapper">
        {post.postPhoto && (
          <img
            src={PF + post.postPhoto}
            alt="#postImage"
            className="singlePostImg"
          />
        )}

        <h1 className="singlePostTitle">
          {post.title}
          {post.username === user?.username && (
            <div className="singlePostEdit">
              <i className="singlePostIcon fa-regular fa-pen-to-square" onClick={()=>setTitle()}></i>
              <i
                className="singlePostIcon fa-regular fa-trash-can"
                onClick={handleDelete}
              ></i>
            </div>
          )}
        </h1>
        <div className="singlePostInfo">
          <span className="singlePostAuthor">
            Author:
            <Link className="link" to={`/?user=${post.username}`}>
              <strong> {post.username}</strong>
            </Link>
          </span>
          <span className="singlePostDate">
            {new Date(post.updatedAt).toDateString()}
          </span>
        </div>
        <p className="singlePostDescription">{post.description}</p>
      </div>
    </div>
  );
}
